mapper.select:
select oo.order_id, oo.order_num, oo.total_price, oo.check_num, oo.pay_price, oo.user_name, oo.create_time, oo.update_time, oo.state, oo.remark, oo.payment_type, oo.express_num, oo.express_type, oo.express_money, oo.consignee, oo.address, oo.phone, oc.express_name as express_name, ex.express_code from weiit_order oo left join weiit_shop_express oc on oo.express_template_id = oc.id left join weiit_init_express_ali ex on oc.express_id = ex.express_id and ex.express_type = 0 where oo.is_deleted=0 and oo.user_id=#{user_id}
and oo.state=#{state}
order BY order_id DESC

select *
from weiit_init_express_ali
where express_code = #{express_code}

select count(*)
from weiit_order_refund r
where r.user_id = #{user_id}

mapper.update:
update weiit_order_refund
set express_template_id=#{express_template_id},
    state=#{state},
    express_num=#{express_num}

where refund_id = #{refund_id}

update weiit_order_refund
set state=#{state}

where refund_id = #{refund_id}

mapper.select:
SELECT *
FROM weiit_shop_payment
WHERE shop_id = #{shop_id}
  AND type = #{payment_type}

SELECT count(*)
FROM weiit_order wo
         LEFT JOIN weiit_order_item oi ON wo.order_num = oi.order_num
WHERE wo.user_id = #{user_id}
  AND wo.is_deleted = 0
  AND product_id = #{product_id}
