mapper.select:
SELECT address_id, contact_name, province, city, district, address, zip_code, remark, phone, tel,
is_deleted, create_time, update_time, create_id, update_id, shipping_type, return_type, shop_id
FROM weiit_shop_address
where is_deleted =0 and address_id=#{address_id}

    and shop_id = #{shop_id}

mapper.update:
update weiit_shop_address set is_deleted=-1
where address_id=#{address_id}

    and shop_id = #{shop_id}

mapper.select:
select months, count(*) userCount , sum(browse) browseCount from ( select DATE_FORMAT(create_time,'%Y%m%d') months,user_id ,count(*) browse from weiit_user_page_log where 1=1
and shop_id = #{shop_id}
and page_type = #{page_type}
and date(create_time) >= date(#{startTime})
and date(create_time) <= date(#{endTime})
group by months,user_id ) temp group by months order by months asc

select ( select count(1) from weiit_order where state=0
and shop_id=#{shop_id}
) no_pay_order_count, ( select count(1) from weiit_order where state=1
and shop_id=#{shop_id}
) no_send_order_count, ( select count(1) from weiit_order_refund WHERE is_deleted=0 and state in (0,1)
and shop_id=#{shop_id}
) after_sale_order_count, ( select ifnull(sum(round(pay_price,2)),0) from weiit_order where state >0
and shop_id=#{shop_id}
AND(to_days(now())-to_days(pay_time)<=7 ) ) day_total , ( select count(*) from weiit_order where state >0
and shop_id=#{shop_id}
AND(to_days(now())-to_days(pay_time)<=7 ) ) day_count from dual

select ( select count(*) mcount from ( select user_id from weiit_user_page_log where 1=1
and shop_id = #{shop_id}
and date(create_time) >= date(#{startTime})
and date(create_time) <= date(#{endTime})
group by user_id ) temp ) log_user_count, ( select count(*) mcount from weiit_user where 1=1
and shop_id = #{shop_id}
and date(create_time) <= date(#{endTime})
) all_user_count, ( select count(*) mcount from weiit_user where 1=1
and shop_id = #{shop_id}
and date(create_time) >= date(#{startTime})
and date(create_time) <= date(#{endTime})
) user_count, ( select count(*) mcount from weiit_user where user_level_id>0
and shop_id = #{shop_id}
and date(create_time) <= date(#{endTime})
) all_card_user_count, ( select count(*) mcount from weiit_user where user_level_id>0
and shop_id = #{shop_id}
and date(create_time) >= date(#{startTime})
and date(create_time) <= date(#{endTime})
) card_user_count, ( select count(*) mcount from ( select count(*) from weiit_order where 1=1
and shop_id = #{shop_id}
and date(pay_time) >= date(#{startTime})
and date(pay_time) <= date(#{endTime})
group by user_id ) temp ) pay_user_count from dual
