mapper.select:
select id as validate_id, file_name, file_url, parent_id, type, create_time, update_time, is_read, business_type, shop_id from weiit_files where type=0
AND parent_id=#{business_type}
AND file_name LIKE #{file_name}
AND (shop_id=#{shop_id} OR shop_id=-1)
ORDER BY create_time DESC

select id as validate_id, file_name, file_url, parent_id, type, create_time, update_time, is_read, business_type, shop_id from weiit_files where type=#{type}
AND (shop_id=#{shop_id} OR shop_id=-1)
ORDER BY create_time DESC
