mapper.select:
select password
from weiit_merchant m
where mrch_id = #{mrch_id}

SELECT *
FROM weiit_merchant_service
where service_num = #{service_num}
  and mrch_id = #{mrch_id}
order by expire_time desc limit 1

SELECT version_id, version_id as validate_id , version_num, version_name, version_logo, smarket_price, sale_price, supply_price, version_desc, state FROM weiit_init_version v where 1=1
and version_id=#{validate_id}
and version_num=#{version_num}
limit 1

mapper.update:
update weiit_merchant
set version_num=#{service_num},
    version_begin_time=#{begin_time},
    version_expire_time=#{expire_time}
where mrch_id = #{mrch_id}

mapper.insert:
insert into weiit_merchant_service ( order_num, service_num, mrch_id, create_time, expire_time, total_price, pay_price, state
) values ( #{order_num}, #{service_num}, #{mrch_id}, #{create_time}, #{expire_time}, #{total_price}, #{pay_price}, #{state} );
select LAST_INSERT_ID() id
