mapper.select:
SELECT *
FROM weiit_weixin_mini_audit_log
WHERE authorizer_app_id = #{authorizer_app_id}
  AND is_current_version = 0
  AND shop_id = #{shop_id}

mapper.update:
update weiit_weixin_mini_testuser
set is_delete= -1,
    update_time=now()
where shop_id = #{shop_id}
  AND id = #{validate_id}

update weiit_weixin_mini_audit_log
set is_current_version= -1,
    update_time=now()
where shop_id = #{shop_id}
  AND authorizer_app_id = #{authorizer_app_id}
  AND is_current_version = 0
