mapper.select:
SELECT rf.refund_id, rf.refund_id AS validate_id, rf.refund_num, rf.refund_imgs, rf.express_num, rf.order_num, it.product_id, it.product_name, it.product_img, it.spec_content, rf.refund_money, rf.create_time, rf.update_time, rf.refund_type, rf.state, rf.user_id, rf.user_name, rf.refund_reason, rf.remark, rf.refusal_reason, se.express_name, ie.express_code, it.sale_price, it.count, it.pay_price FROM weiit_order_refund rf INNER JOIN weiit_order_item it ON rf.order_item_id = it.id LEFT JOIN weiit_shop_express se ON rf.express_template_id = se.id LEFT JOIN weiit_init_express_ali ie ON se.express_id = ie.express_id where 1=1
and rf.shop_id=#{shop_id}
and rf.refund_id =#{validate_id}

mapper.update:
update weiit_order_refund set
update_time=now() ,
state=#{state} ,
refusal_reason =#{refusal_reason}
where refund_id=#{validate_id}

    and shop_id=#{shop_id}

mapper.select:
SELECT rf.refund_id, rf.refund_id AS validate_id, rf.refund_num, rf.refund_imgs, rf.express_num, rf.order_num, it.product_id, it.product_name, it.product_img, it.spec_content, rf.refund_money, rf.create_time, rf.update_time, rf.refund_type, rf.state, rf.user_id, rf.user_name, rf.refund_reason, rf.remark, iu.balance, rf.refusal_reason, it.sale_price, it.count, it.pay_price FROM weiit_order_refund rf INNER JOIN weiit_order_item it ON rf.order_item_id = it.id INNER JOIN weiit_user iu ON rf.user_id = iu.user_id where 1=1
and rf.shop_id=#{shop_id}
and rf.refund_id =#{validate_id}
