mapper.select:
SELECT group_id, group_id AS validate_id, group_name, state, create_time, update_time, shop_id FROM weiit_product_group where 1=1
and shop_id =#{shop_id}
and group_id=#{validate_id}

mapper.delete:
delete from weiit_product_group
where group_id=#{validate_id}

    and shop_id =#{shop_id}

mapper.update:
update weiit_product_group
set
update_time=now()

    ,group_name=#{group_name}


    ,state=#{state}

where group_id=#{validate_id}

    and shop_id =#{shop_id}

mapper.select:
SELECT id, group_id, product_id, create_time, shop_id FROM weiit_product_group_item where 1=1
and group_id=#{group_id}
and product_id=#{product_id}
and shop_id =#{shop_id}
order by id asc

mapper.delete:
delete from weiit_product_group_item
where 1=1

    and group_id =#{validate_id}


    and product_id =#{product_id}


    and shop_id =#{shop_id}

mapper.select:
SELECT gg.group_name, wp.product_img, wp.product_name, wp.sale_price, wp.sale_count, wp.smarket_price, pg.group_id, wp.is_on_sale, wp.is_deleted, wp.is_disabled, wp.product_id FROM weiit_product_group_item pg LEFT JOIN weiit_product_group gg ON gg.group_id = pg.group_id LEFT JOIN weiit_product wp ON pg.product_id = wp.product_id WHERE gg.state =0
and gg.shop_id =#{shop_id}
and pg.group_id =#{group_id}
