mapper.select:
SELECT tag_id, tag_id AS validate_id, tag_name, state, create_time, update_time, shop_id FROM weiit_product_tag WHERE 1 = 1
and shop_id =#{shop_id}
and tag_id=#{validate_id}

mapper.delete:
delete from weiit_product_tag
where tag_id=#{validate_id}

    and shop_id =#{shop_id}

mapper.update:
update weiit_product_tag
set
update_time=now()

    ,tag_name=#{tag_name}


    ,state=#{state}

where tag_id=#{validate_id}

    and shop_id =#{shop_id}

mapper.select:
SELECT ti.id, ti.tag_id, ti.product_id, ti.create_time, ti.shop_id, ta.tag_name FROM weiit_product_tag_item ti, weiit_product_tag ta WHERE ti.tag_id = ta.tag_id
and ti.tag_id=#{tag_id}
and ti.product_id=#{product_id}
and ti.shop_id =#{shop_id}
order by ti.id asc

mapper.delete:
delete from weiit_product_tag_item
where 1=1

    and tag_id =#{validate_id}


    and product_id =#{product_id}


    and shop_id =#{shop_id}
