mapper.select:
SELECT id, id AS validate_id, protocol_title, protocol_content, type, is_deleted, create_time, update_time, shop_id FROM weiit_shop_protocol WHERE is_deleted = 0
AND shop_id = #{shop_id}
ORDER BY create_time DESC

SELECT id, id AS validate_id, protocol_title, protocol_content, type, is_deleted, create_time, update_time, shop_id FROM weiit_shop_protocol WHERE is_deleted = 0 AND id = #{validate_id}
and shop_id = #{shop_id}
order by create_time desc
