mapper.select:
SELECT id, id AS validate_id, state, nav_name, nav_page, nav_type, nav_bgcolor, create_time, update_time, shop_id FROM weiit_page_nav WHERE 1 = 1
and shop_id = #{shop_id}
order by id asc

mapper.update:
update weiit_page_nav set
state=#{state}
where 1=1

    and id = #{validate_id}


    and shop_id = #{shop_id}

mapper.insert:
insert into weiit_page_nav
(
state, nav_name, nav_page, nav_type, nav_bgcolor, create_time, update_time, shop_id
)
values
(
#{state}, #{nav_name},#{nav_page},#{nav_type},#{nav_bgcolor},now(),now(),#{shop_id}
);

    select LAST_INSERT_ID() nav_id

mapper.delete:
delete from weiit_page_nav
where id=#{validate_id}

    and shop_id = #{shop_id}

mapper.select:
SELECT item_id, nav_id, item_name, item_img1, item_img2, item_target_type, item_target_id, item_target_url, parent_id, sort_num, create_time, update_time, shop_id, ( SELECT nav_type FROM weiit_page_nav na WHERE na.id = it.nav_id ) AS nav_type FROM weiit_page_nav_item it WHERE 1 = 1
and shop_id = #{shop_id}
and nav_id = #{validate_id}
order by item_id asc

mapper.insert:
insert into weiit_page_nav_item
(
nav_id ,item_name, item_img1, item_img2, item_target_type,
item_target_id, item_target_url, parent_id, sort_num, create_time, update_time, shop_id
)
values
(
#{nav_id}, #{item_name},#{item_img1},#{item_img2},#{item_target_type},
#{item_target_id},#{item_target_url},#{parent_id},#{sort_num}, now(),now(),#{shop_id}
);

    select LAST_INSERT_ID() item_id

mapper.update:
delete from weiit_page_nav_item
where nav_id=#{validate_id}

    and shop_id = #{shop_id}
