mapper.select:
SELECT us.bargain_id, us.bargain_id AS validate_id, us.product_id, op.product_name, op.product_img, us.bargain_name, us.start_time, us.end_time, us.max_price, us.min_price, pi.stock, us.cut_price_min, us.cut_price_max, us.state, us.create_time, us.update_time, us.shop_id FROM weiit_ump_bargain us, weiit_product op, weiit_product_item pi WHERE us.product_id = op.product_id AND us.item_id = pi.item_id
and us.bargain_name like concat('%', #{bargain_name}, '%')
and us.state = #{state}
and us.shop_id=#{shop_id}
order by us.create_time desc

mapper.update:
update weiit_ump_bargain set
state=#{state} ,
product_id = #{product_id}, bargain_name = #{bargain_name}, start_time=#{start_time}, end_time=#{end_time}, max_price = #{max_price}, min_price = #{min_price}, cut_price_min = #{cut_price_min}, cut_price_max = #{cut_price_max}, bargain_hour = #{bargain_hour}, item_id = #{item_id}, state=#{state},
update_time=now() where bargain_id=#{validate_id}
and shop_id=#{shop_id}

mapper.select:
SELECT bo.b_order_id validate_id, bo.pay_price, bo.state, bo.pay_time, bo.order_num, us.user_name, us.user_img, ub.bargain_name, ub.max_price, ub.start_time, ub.end_time, p.product_name, p.product_img FROM weiit_ump_bargain_order bo LEFT JOIN weiit_ump_bargain ub ON ub.bargain_id = bo.bargain_id LEFT JOIN weiit_user us ON bo.user_id = us.user_id LEFT JOIN weiit_product p ON ub.product_id = p.product_id WHERE ub.bargain_id = #{validate_id}
and bo.shop_id=#{shop_id}
and bo.order_num like concat('%', #{order_num}, '%')
ORDER BY bo.start_time DESC

SELECT ub.bargain_id, ub.bargain_name, ub.end_time, ub.max_price, ub.min_price, wp.product_img, wp.product_name, wp.sale_count FROM weiit_ump_bargain ub LEFT JOIN weiit_product wp ON ub.product_id = wp.product_id WHERE wp.is_on_sale=0 AND ub.state=0 AND ub.shop_id=#{shop_id}
AND end_time >#{end_time}
and ub.bargain_id IN
#{bargain_id}
