mapper.select:
select coupon_id, coupon_id as validate_id, coupon_num, coupon_name, type, coupon_price, coupon_discount, random_min_price, random_max_price, is_condition, condition_price, coupon_desc, use_type, from_day, to_day, start_time, end_time, number, product_ids, state, max_limit, create_time, update_time, (select count(1) from weiit_ump_coupon_user tt where tt.coupon_id = t.coupon_id ) as distribute_count, (select count(1) from weiit_ump_coupon_user tt where tt.coupon_id = t.coupon_id and tt.state=1) as used_count from weiit_ump_coupon t where 1=1
and coupon_name like concat('%', #{coupon_name}, '%')
and state = #{state}
and shop_id=#{shop_id}
order by update_time desc

mapper.update:
update weiit_ump_coupon set
state=#{state} ,
coupon_name=#{coupon_name}, coupon_num=#{coupon_num}, type=#{type}, coupon_price=#{coupon_price}, coupon_discount=#{coupon_discount}, random_min_price=#{random_min_price}, random_max_price=#{random_max_price}, is_condition=#{is_condition}, condition_price=#{condition_price}, coupon_desc=#{coupon_desc}, use_type=#{use_type}, from_day=#{from_day}, to_day=#{to_day}, start_time=#{start_time}, end_time=#{end_time}, number = #{number}, product_range_type=#{product_range_type}, product_ids = #{product_ids}, state=#{state}, max_limit=#{max_limit},
update_time=now() where coupon_id=#{validate_id}
and shop_id=#{shop_id}

mapper.select:
select u.id, u.coupon_id, u.user_id, us.user_name, us.user_account, us.user_img, u.state, u.create_time, u.update_time, u.order_num, u.remark, c.type, c.coupon_price, c.coupon_discount, c.random_min_price, c.random_max_price, c.is_condition, c.condition_price from weiit_ump_coupon_user u inner join weiit_ump_coupon c on u.coupon_id=c.coupon_id inner join weiit_user us on u.user_id=us.user_id where 1=1
and u.coupon_id=#{validate_id}
and u.id=#{id}
and u.state=#{state}
and us.user_account like concat('%', #{user_account}, '%')
and u.shop_id=#{shop_id}
order by u.create_time desc

select coupon_id, coupon_num, coupon_name, type, coupon_price, coupon_discount, random_min_price, random_max_price, is_condition, condition_price, coupon_desc, use_type, from_day, to_day, start_time, end_time, number, product_range_type, product_ids, state, max_limit, shop_id from weiit_ump_coupon where state=1
AND shop_id=#{shop_id}
AND end_time=#{end_time}
AND coupon_id IN
#{coupon_id}
