mapper.select:
SELECT us.groupon_id, us.groupon_id AS validate_id, us.product_id, op.product_name, op.product_img, op.stock, us.groupon_price, us.groupon_name, us.start_time, us.end_time, us.expire_time, us.groupon_number, us.max_limit, us.virtual_groupon, us.head_discount, us.state, us.create_time, us.update_time, us.shop_id FROM weiit_ump_groupon us, weiit_product op where us.product_id=op.product_id AND us.is_delete=0
and us.groupon_name like concat('%', #{groupon_name}, '%')
and us.state = #{state}
and us.shop_id=#{shop_id}
order by us.create_time desc

mapper.update:
update weiit_ump_groupon set
state=#{state} ,
product_id = #{product_id}, groupon_price = #{groupon_price}, groupon_name = #{groupon_name}, start_time=#{start_time}, end_time=#{end_time}, expire_time = #{expire_time}, groupon_number = #{groupon_number}, max_limit = #{max_limit}, virtual_groupon = #{virtual_groupon}, head_discount = #{head_discount}, state=#{state},
update_time=now() where groupon_id=#{validate_id}
and shop_id=#{shop_id}

UPDATE weiit_ump_groupon_item SET
groupon_price = #{groupon_price},
is_delete = #{is_delete},
update_time=now() WHERE groupon_id = #{groupon_id}
AND item_id = #{item_id}

mapper.delete:
UPDATE weiit_ump_groupon
SET is_delete = #{is_delete}
WHERE groupon_id = #{validate_id}

mapper.select:
SELECT de.g_order_id, us.user_id, us.user_name, us.user_img, ug.groupon_name, ug.groupon_number, ug.groupon_price, ug.expire_time, de.create_time, de.state FROM weiit_ump_groupon_order de, weiit_ump_groupon ug, weiit_user us WHERE ug.groupon_id = de.groupon_id AND de.user_id = us.user_id AND ug.groupon_id = #{validate_id} AND de.parent_id=-1
and de.shop_id=#{shop_id}
order by de.create_time desc

SELECT na.g_order_id, us.user_id, us.user_name, us.user_img, na.create_time, na.order_num, na.pay_time, na.end_time, na.state FROM weiit_ump_groupon_order na, weiit_user us WHERE na.user_id = us.user_id AND (na.g_order_id = #{g_order_id} OR na.parent_id=#{g_order_id})
and na.shop_id=#{shop_id}
order by na.create_time desc

SELECT ug.groupon_id, ug.product_id, ug.groupon_name, ug.groupon_number, ug.groupon_price, wp.product_img, wp.product_name, wp.sale_count, wp.sale_price FROM weiit_ump_groupon ug LEFT JOIN weiit_product wp ON ug.product_id = wp.product_id WHERE ug.shop_id = #{shop_id}
AND groupon_id IN
#{groupon_id}

SELECT *
FROM weiit_ump_groupon_item ug
WHERE groupon_id = #{validate_id}
