mapper.update:
update weiit_ump_integral_product set
product_id = #{product_id},
item_id = #{item_id},
sale_price = #{sale_price},
integral = #{integral},
integral_price = #{integral_price},
max_limit = #{max_limit},
state=#{state}
where integral_id=#{validate_id}

    and shop_id=#{shop_id}

update weiit_ump_integral_product set
state=#{state}
where integral_id=#{validate_id}

    and shop_id=#{shop_id}

mapper.select:
SELECT io.i_order_id AS validate_id, io.i_order_id, io.user_id, io.product_id, io.item_id, io.count, io.pay_price, io.integral, io.address_id, io.order_num, io.pay_time, o.state, p.product_name, p.sale_price, p.product_img, a.express_code, a.express_name, o.express_num, io.shop_id FROM weiit_ump_integral_order io LEFT JOIN weiit_product p ON p.product_id = io.product_id LEFT JOIN weiit_order o ON io.order_num = o.order_num LEFT JOIN weiit_init_express_ali a ON a.express_id = o.express_template_id WHERE 1 = 1 AND io.shop_id=#{shop_id}
and o.order_num like concat('%', #{order_num}, '%')
and o.state like concat('%', #{state}, '%')
