mapper.select:
select id, id as validate_id, card_name, card_bg_type, card_bg_color, card_img, is_free_shippiing, discount, give_integral, use_remark, tel, type, order_count, order_total, integral_count, limit_type, start_time, end_time, is_disabled, is_deleted, create_time, update_time, shop_id from weiit_user_card_info where is_deleted =0
and shop_id = #{shop_id}
order by create_time desc

mapper.update:
update weiit_user_card_info
set is_deleted=-1
where
id=#{validate_id}

    and shop_id = #{shop_id}

mapper.select:
select uc.id, uc.id as validate_id, us.user_id, us.user_account, us.user_name, us.user_nike, us.user_phone, us.user_img, us.balance, us.integral, us.create_time, us.update_time, us.is_disabled, us.region, ci.card_name from weiit_user us, weiit_user_card_info ci, weiit_user_card uc where us.user_id=uc.user_id and uc.card_id=ci.id
and ci.id = #{validate_id}
and user_name like concat('%', #{user_name}, '%')
and us.shop_id = #{shop_id}
order by us.create_time desc
