mapper.select:
SELECT cl.cash_id, cl.cash_id AS validate_id, cl.cash_num, cl.cash_type, cl.bank_name, cl.cash_name, cl.cash_account, cl.cash_money, cl.remark, cl.state, cl.reason, cl.create_time, cl.update_time, cl.shop_id, us.user_id, us.user_name FROM weiit_user_cash_log cl, weiit_user us WHERE cl.user_id = us.user_id
and cl.shop_id = #{shop_id}
and cl.state = #{state}
and us.user_name like concat('%', #{user_name}, '%')
order by cl.create_time desc

mapper.update:
update weiit_user_cash_log set
state = #{state} ,
reason = #{reason} ,
update_time =now()
where cash_id=#{validate_id}

    and shop_id = #{shop_id}
