mapper.select:
SELECT
*
from
weiit_weixin_public_info
WHERE
1 = 1

    and service_type_info=#{service_type_info}


    and authorizer_app_id=#{authorizer_app_id}


    and shop_id=#{shop_id}

limit 1

mapper.update:
update
    weiit_weixin_mini_intropage
set is_use=1
where id = #{validate_id}
  and shop_id = #{shop_id}
