mapper.select:
SELECT reply_id, reply_type,rule_name,match_type, key_name, reply_content, type_id, authorizer_id FROM weiit_weixin_public_reply where authorizer_id=#{authorizer_id} and type_id=#{action_type}
and reply_type=#{reply_type}
and ( rule_name like concat('%', #{key_name}, '%') or key_name like concat('%', #{key_name}, '%') )

SELECT image_id, image_url, media_id, create_time, update_time, authorizer_id
FROM weiit_weixin_public_media_image
WHERE authorizer_id=#{authorizer_id}

    and media_id=#{media_id}

order by create_time desc

SELECT id, title, author, cover_img , media_id, create_time
FROM weiit_weixin_public_media_article
where authorizer_id=#{authorizer_id}

    and title like concat('%',#{title},'%')


    and media_id=#{media_id}

order by create_time desc

mapper.update:
update weiit_weixin_public_reply_type
set state=#{state}
where authorizer_id = #{authorizer_id}
  and action_type = #{action_type}

mapper.delete:
delete
from weiit_weixin_public_reply
where reply_id = #{reply_id}
  and authorizer_id = #{authorizer_id}
