mapper.select:
select cate_id, cate_id as validate_id, cate_name, cate_img, cate_icon_img, parent_id, is_show, create_time, update_time, sort_num from weiit_init_product_cate where is_deleted=0
and parent_id=#{parent_id}
and is_show =#{is_show}
order by parent_id asc , sort_num desc , cate_id asc

mapper.update:
update weiit_init_product_cate
set
update_time=now()

    ,cate_name=#{cate_name}


    ,cate_img=#{cate_img}


    ,cate_icon_img=#{cate_icon_img}


    ,parent_id=#{parent_id}


    ,is_show=#{is_show}


    ,sort_num=#{sort_num}

where
is_deleted=0
and cate_id=#{validate_id}
