mapper.select:
SELECT service_id, service_id as validate_id , service_num, service_name, service_smarket_price, service_price, service_icon, service_logo, remark, service_desc, link_url, sort_num, state, type FROM weiit_init_service se where 1=1
and service_id=#{validate_id}
and service_num=#{service_num}
limit 1

mapper.update:
update weiit_init_service
set
update_manager_id= #{manager_id} ,
update_time=now()

    ,service_name=#{service_name}


    ,service_smarket_price=#{service_smarket_price}


    ,service_price=#{service_price}


    ,type=#{type}


    ,service_logo=#{service_logo}


    ,service_icon=#{service_icon}


    ,remark=#{remark}


    ,link_url=#{link_url}


    ,sort_num=#{sort_num}


    ,service_desc=#{editorValue}


    ,state=#{state}

where service_id=#{validate_id}

update weiit_init_order_time
set order_time_discount=#{order_time_discount},
    state=#{state}
where order_time_name = #{order_time_name}

mapper.select:
SELECT version_id, version_id as validate_id , version_num, version_name, version_logo, smarket_price, sale_price, supply_price, version_desc, state FROM weiit_init_version v where 1=1
and version_id=#{validate_id}
and version_num=#{version_num}
limit 1

mapper.update:
update weiit_init_version
set
update_manager_id= #{manager_id} ,
update_time=now()

    ,version_name=#{version_name}


    ,smarket_price=#{smarket_price}


    ,sale_price=#{sale_price}


    ,supply_price=#{supply_price}


    ,version_logo=#{version_logo}


    ,version_desc=#{editorValue}


    ,state=#{state}

where version_id=#{validate_id}
