mapper.select:
select manager_id, manager_name, account_name, password, description, create_by, create_time, update_time, type, parent_id, locked, state, is_deleted , (select sr.role_sign from system_role sr, system_manager_role ro where sr.role_id=ro.role_id and ro.manager_id=sm.manager_id ) as role_sign from system_manager sm where 1=1 and is_deleted=0
and manager_id=#{manager_id}
and account_name=#{account_name}

select shop_id
from shop_manager
where manager_id = #{manager_id}

select app_id
from system_manager_developer
where manager_id = #{manager_id}

select id, num, balance, last_balance, remark, type, state, create_time, update_time, (select manager_name from system_manager where manager_id=b.manager_id) as manager_name, manager_id from system_manager_balance b where manager_id=#{manager_id}
and type=#{type}
order by create_time desc
