mapper.select:
SELECT
groupon_id
FROM
weiit_ump_groupon
WHERE state=#{state}

    AND end_time < #{end_time}


    AND create_time > #{create_time}

SELECT ugo.groupon_id, ugo.order_num, ugo.check_num, ugo.item_id, ugo.count, ugo.g_order_id, ugo.pay_price, ugo.pay_time, ugo.user_id, ugo.shop_id, ugo.address_id, ugo.payment_type, ugo.product_id, wp.product_name, wp.smarket_price, wp.product_img, wp.sale_price, wu.user_name , wu.wx_open_id , info.authorizer_id ,info.authorizer_app_id, info.service_type_info FROM weiit_ump_groupon_order ugo, weiit_product wp , weiit_user wu, weiit_weixin_public_info info WHERE ugo.product_id = wp.product_id and ugo.user_id=wu.user_id and wu.shop_id=info.shop_id and wu.open_id_type=info.service_type_info and ugo.state=#{state}
AND ugo.end_time < #{end_time}
AND ugo.groupon_id = #{groupon_id}
AND ugo.parent_id = #{parent_id}
AND (ugo.g_order_id = #{g_order_id} OR ugo.parent_id=#{g_order_id})

select ms.template_msg_id, ms.wx_template_id
from weiit_weixin_public_template_type tt,
     weiit_weixin_public_template_msg ms
where tt.template_type_id = ms.template_type_id
  AND ms.state = 1
  and tt.business_type = #{business_type}
  AND tt.template_type = #{template_type}
  AND ms.authorizer_id = #{authorizer_id}
order by template_msg_id desc limit 1

mapper.update:
update weiit_ump_groupon_order
SET state=#{update_state}
WHERE 1=1

    AND g_order_id=#{g_order_id}


    AND state=#{state}


    AND create_time=#{create_time}
